import csv
import os
from io import StringIO


def append_raw_block_to_csv(block_text: str, filename: str = "queries_raw.csv"):
    """
    –î–æ–±–∞–≤–ª—è–µ—Ç –±–ª–æ–∫ –∑–∞–ø—Ä–æ—Å–æ–≤ –≤ CSV-—Ñ–∞–π–ª.
    –ü–µ—Ä–≤—É—é —Å—Ç—Ä–æ–∫—É (–∑–∞–≥–æ–ª–æ–≤–æ–∫) –ø—Ä–æ–ø—É—Å–∫–∞–µ—Ç.
    –û—Å—Ç–∞–ª—å–Ω—ã–µ —Å—Ç—Ä–æ–∫–∏ –¥–æ–ø–∏—Å—ã–≤–∞–µ—Ç –∫–∞–∫ –µ—Å—Ç—å.
    """
    lines = block_text.strip().split("\n")

    # –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –∑–∞–≥–æ–ª–æ–≤–æ–∫, –µ—Å–ª–∏ –æ–Ω –µ—Å—Ç—å
    if lines and lines[0].strip().lower() == "query_id,text":
        data_lines = lines[1:]
    else:
        data_lines = lines

    # –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ —Ñ–∞–π–ª —Å—É—â–µ—Å—Ç–≤—É–µ—Ç –∏–ª–∏ —Å–æ–∑–¥–∞–¥–∏–º —Å –∑–∞–≥–æ–ª–æ–≤–∫–æ–º
    file_exists = os.path.exists(filename)
    if not file_exists:
        with open(filename, "w", encoding="utf-8", newline="") as f:
            writer = csv.writer(f)
            writer.writerow(["query_id", "text"])

    # –î–æ–ø–∏—Å—ã–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ
    with open(filename, "a", encoding="utf-8", newline="") as f:
        writer = csv.writer(f)
        for line in data_lines:
            line = line.strip()
            if not line:
                continue
            # –ü–∞—Ä—Å–∏–º —Å—Ç—Ä–æ–∫—É –∫–∞–∫ CSV (—á—Ç–æ–±—ã –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å –∑–∞–ø—è—Ç—ã–µ –≤–Ω—É—Ç—Ä–∏ –∫–∞–≤—ã—á–µ–∫)
            reader = csv.reader(StringIO(line))
            row = next(reader, None)
            if row and len(row) >= 2:
                writer.writerow([row[0], row[1]])
            elif row and len(row) == 1:
                # –ù–∞ —Å–ª—É—á–∞–π, –µ—Å–ª–∏ text –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç
                writer.writerow([row[0], ""])
            # –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º –ø—É—Å—Ç—ã–µ —Å—Ç—Ä–æ–∫–∏

    print(f"–î–æ–±–∞–≤–ª–µ–Ω–æ {len(data_lines)} —Å—Ç—Ä–æ–∫ –≤ {filename}.")


